import { configureStore } from '@reduxjs/toolkit';
import gameReducer from './reducers/gameReducer';

const initialState = {
    game: {
        balance: 1000,
        bet: 0,
        dealerHand: [{value: 'A', suit: 'hearts'}, {value: '5', suit: 'diamonds'}],
        playerHand: [{value: '9', suit: 'hearts'}, {value: '2', suit: 'diamonds'}],
        currentDealerScore: 16,
        currentPlayerScore: 11,
        gameStage: 'ready',
        deck: []
    }
};


// const store = configureStore({
//   reducer: {
//     game: gameReducer,
//   },
//   preloadedState: initialState, // Make sure this value is not undefined
// });

const rootReducer = {
    game: gameReducer,
};

const store = configureStore({
    reducer: rootReducer,
    preloadedState: initialState,
});

export default store;
