import { getDeck, shuffleDeck, getCardValue } from '../utils/deckUtils';

export const setBet = (bet) => {
  return {
    type: 'SET_BET',
    payload: bet
  };
};

export const dealCards = () => (dispatch) => {
  const deck = getDeck();
  const shuffledDeck = shuffleDeck(deck);

  const playerCard1 = shuffledDeck.pop();
  const dealerCard1 = shuffledDeck.pop();
  const playerCard2 = shuffledDeck.pop();
  const hiddenCard = shuffledDeck.pop();  

  const currentDealerScore = getCardValue(dealerCard1) + getCardValue(hiddenCard);
  const currentPlayerScore = getCardValue(playerCard1) + getCardValue(playerCard2);

  // remove the cards from the deck (we have removed 4 cards)
  const newDeck = shuffledDeck.slice(4);
  const playerHand = [playerCard1, playerCard2];
  const dealerHand = [dealerCard1, hiddenCard];

  dispatch({
    type: 'DEAL_CARDS',
    payload: {
      newDeck,
      dealerHand,
      playerHand,
      currentDealerScore,
      currentPlayerScore
    }
  });
};



export const hit = () => (dispatch, getState) => {
  const { game } = getState();
  const deck = game.deck;

  const nextCard = getNextCard(deck);
  // get the players current cards
  const playerHand = game.playerHand;
  // add the next card to the players hand
  const newPlayerHand = [...playerHand, nextCard];
  const currentPlayerScore = getCardValue(playerCard1) + getCardValue(playerCard2);

