import React from 'react';

function Hand({ cards, score, isDealer }) {
  return (
    <div className={`hand ${isDealer ? 'dealer' : 'player'}`}>
      <h3 className="hand-title">{isDealer ? 'Dealer' : 'Player'}</h3>
      <div className="hand-cards">
        {cards.map((card, index) => (
          <img key={index} src={card.image} alt={`${card.value} of ${card.suit}`} />
        ))}
      </div>
      <div className="hand-score">{isDealer || !cards.length ? '' : score}</div>
    </div>
  );
}

export default Hand;
