import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setBet } from '../actions/gameActions';

function BetForm() {
  const dispatch = useDispatch();
  const game = useSelector(state => state.game);
  const [bet, setBetValue] = useState(game.bet);

  const handleSubmit = event => {
    event.preventDefault();
    dispatch(setBet(bet));
  };

  const handleInputChange = event => {
    setBetValue(parseInt(event.target.value));
  };

  return (
    <form className="bet-form" onSubmit={handleSubmit}>
      <label htmlFor="bet-input">Bet:</label>
      <input type="number" id="bet-input" name="bet" value={bet} onChange={handleInputChange} min="1" max={game.balance} />
      <button type="submit" className="btn btn-primary">
        Place Bet
      </button>
    </form>
  );
}

export default BetForm;
