export const setBet = bet => ({
    type: 'SET_BET',
    payload: bet
  });

  export const dealCards = (dealerCard, hiddenCard, playerCard1, playerCard2, dealerScore, playerScore) => ({
    type: 'DEAL_CARDS',
    payload: { dealerCard, hiddenCard, playerCard1, playerCard2, dealerScore, playerScore }
  });

  export const hit = (card, score) => ({
    type: 'HIT',
    payload: { card, score }
  });

  export const stand = (hand, score) => ({
    type: 'STAND',
    payload: { hand, score }
  });

  export const resetGame = () => ({
    type: 'RESET_GAME'
  });
