import React from 'react';
import Hand from './Hand';
import Controls from './Control';
import BetForm from './BetForm';
import Balance from './Balance';
import { useDispatch, useSelector } from 'react-redux';
import { dealCards, hit, stand, resetGame } from '../actions/gameActions';

function Game() {
  const dispatch = useDispatch();
  const game = useSelector(state => state.game);

  const handleDealClick = () => {
    dispatch(dealCards());
  };

  const handleHitClick = () => {
    dispatch(hit());
  };

  const handleStandClick = () => {
    dispatch(stand());
  };

  const handleResetClick = () => {
    dispatch(resetGame());
  };

  return (
    <div>
      <Balance balance={game.balance} />
      <Hand cards={game.dealerHand} score={game.currentDealerScore} isDealer={true} />
      <Hand cards={game.playerHand} score={game.currentPlayerScore} isDealer={false} />
      <Controls
        gameStage={game.gameStage}
        handleDealClick={handleDealClick}
        handleHitClick={handleHitClick}
        handleStandClick={handleStandClick}
        handleResetClick={handleResetClick}
      />
    <BetForm />
    </div>
  );
}

export default Game;
