export const setBet = (amount) => ({
  type: 'SET_BET',
  payload: amount
});

const getCardValue = (card) => {
  if (['JACK', 'QUEEN', 'KING'].includes(card.rank)) {
    return 10;
  } else if (card.rank === 'ACE') {
    return 11;
  } else {
    return parseInt(card.rank);
  }
};

export const dealCards = () => (dispatch, getState) => {
  const { deck } = getState().game;
  // print out the deck to see what's in it
  console.log(deck);
  const dealerCard1 = deck.pop();
  const dealerCard2 = deck.pop();
  const playerCard1 = deck.pop();
  const playerCard2 = deck.pop();
  const dealerScore = getCardValue(dealerCard1) + getCardValue(dealerCard2);
  const playerScore = getCardValue(playerCard1) + getCardValue(playerCard2);
  dispatch({
    type: 'DEAL_CARDS',
    payload: {
      dealerCard: dealerCard1,
      hiddenCard: dealerCard2,
      playerCard1,
      playerCard2,
      dealerScore,
      playerScore
    }
  });
};

export const hit = () => (dispatch, getState) => {
  const { deck, playerHand, currentPlayerScore } = getState().game;
  const card = deck.pop();
  const score = getCardValue(card) + currentPlayerScore;
  const newHand = [...playerHand, card];
  dispatch({
    type: 'HIT',
    payload: {
      card,
      score,
      hand: newHand
    }
  });
};

export const stand = () => (dispatch, getState) => {
  const { deck, dealerHand, currentDealerScore } = getState().game;
  let score = currentDealerScore;
  let hand = [...dealerHand];
  while (score < 17) {
    const card = deck.pop();
    hand.push(card);
    score += getCardValue(card);
  }
  dispatch({
    type: 'STAND',
    payload: {
      score,
      hand
    }
  });
};

export const resetGame = () => ({
  type: 'RESET_GAME'
});
