import React from 'react';

function Controls({ gameStage, handleDealClick, handleHitClick, handleStandClick, handleResetClick }) {
  return (
    <div className="controls">
      {gameStage === 'ready' && (
        <button className="btn btn-primary" onClick={handleDealClick}>
          Deal
        </button>
      )}
      {gameStage === 'playing' && (
        <>
          <button className="btn btn-primary" onClick={handleHitClick}>
            Hit
          </button>
          <button className="btn btn-primary" onClick={handleStandClick}>
            Stand
          </button>
        </>
      )}
      {gameStage === 'result' && (
        <>
          <button className="btn btn-secondary" onClick={handleResetClick}>
            Reset
          </button>
        </>
      )}
    </div>
  );
}

export default Controls;
