import { getDeck, shuffleDeck, getCardValue, getHandValue } from '../utils/deckUtils';

export const setBet = (bet) => {
  return {
    type: 'SET_BET',
    payload: bet
  };
};

export const dealCards = () => (dispatch) => {
  const deck = getDeck();
  const shuffledDeck = shuffleDeck(deck);

  const playerCard1 = shuffledDeck.pop();
  const hiddenCard = shuffledDeck.pop();
  const playerCard2 = shuffledDeck.pop();
  const dealerCard1 = shuffledDeck.pop();

  // remove the cards from the deck (we have removed 4 cards)
  const newDeck = shuffledDeck.slice(4);
  const playerHand = [playerCard1, playerCard2];
  const dealerHand = [dealerCard1, hiddenCard];

  const currentDealerScore = getCardValue(dealerCard1);
  const currentPlayerScore = getHandValue(playerHand);

  dispatch({
    type: 'DEAL_CARDS',
    payload: {
      newDeck,
      dealerHand,
      playerHand,
      currentDealerScore,
      currentPlayerScore
    }
  });
};



export const hit = () => (dispatch, getState) => {
  const { game } = getState();
  const deck = game.deck;

  const nextCard = getNextCard(deck);
  // get the players current cards
  const playerHand = game.playerHand;
  // add the next card to the players hand
  const newPlayerHand = [...playerHand, nextCard];
  const newScore = getHandValue(newPlayerHand);

  dispatch({
    type: 'HIT',
    payload: {
      card: nextCard,
      score: newScore
    }
  });

  console.log('newScore', newScore);

  if (newScore > 21) {
    dispatch({
      type: 'BUST'
    });
  }

  // Remove the next card from the deck
  const newDeck = deck.slice(1);
  dispatch({
    type: 'UPDATE_DECK',
    payload: newDeck
  });
};

export const getNextCard = (deck) => {
  return deck[0];
}



export const stand = () => (dispatch, getState) => {
  const { game } = getState();
  let deck = game.deck;
  let dealerHand = game.dealerHand;
  let dealerScore = game.currentDealerScore;

  // Reveal hidden card and update dealer score
  const hiddenCard = dealerHand[1];
  dealerScore += getCardValue(hiddenCard);

  // Dealer must hit until they have 17 or more
  // without while loop
  if (dealerScore < 17) {
    const nextCard = getNextCard(deck);
    dealerHand = [...dealerHand, nextCard];
    dealerScore += getCardValue(nextCard);
    deck = deck.slice(1);
  }



  dispatch({
    type: 'STAND',
    payload: {
      hand: dealerHand,
      score: dealerScore
    }
  });
};

export const resetGame = () => {
  return {
    type: 'RESET_GAME'
  };
};
