import { configureStore } from '@reduxjs/toolkit';
import gameReducer from './reducers/gameReducer';

const initialState = {
    game: {
        balance: 1000,
        bet: 0,
        dealerHand: [],
        playerHand: [],
        currentDealerScore: 16,
        currentPlayerScore: 11,
        gameStage: 'ready',
        deck: []
    }
};


// const store = configureStore({
//   reducer: {
//     game: gameReducer,
//   },
//   preloadedState: initialState, // Make sure this value is not undefined
// });

const rootReducer = {
    game: gameReducer,
};

const store = configureStore({
    reducer: rootReducer,
    preloadedState: initialState,
});

export default store;
