import React from 'react';
import Card from './Card';

function Hand({ cards = [], score, isDealer }) {
  return (
    <div className={isDealer ? 'dealer-hand' : 'player-hand'}>
      <h2>{isDealer ? 'Dealer' : 'Player'}</h2>
      <div className="cards">
        <span>
            {cards.map((card, index) => (
                <h4> {card.value} of {card.suit} </h4>
            ))}
        </span>
      </div>
      <div className="score">
        <span>{score}</span>
      </div>
    </div>
  );
}

export default Hand;
