import { createStore } from 'redux';
import gameReducer from './reducers/gameReducer';

const initialState = {
  balance: 1000,
  bet: 0,
  dealerHand: [],
  playerHand: [],
  currentDealerScore: 0,
  currentPlayerScore: 0,
  gameStage: 'ready'
};

const store = createStore(gameReducer, initialState);

export default store;
