import { configureStore } from '@reduxjs/toolkit';


const gameReducer = (state, action) => {
  switch (action.type) {
    case 'SET_BET':
      return {
        ...state,
        bet: action.payload
      };
    case 'DEAL_CARDS':
      return {
        ...state,
        dealerHand: [action.payload.dealerCard, action.payload.hiddenCard],
        playerHand: [action.payload.playerCard1, action.payload.playerCard2],
        currentDealerScore: action.payload.dealerScore,
        currentPlayerScore: action.payload.playerScore,
        gameStage: 'playing'
      };
    case 'HIT':
      return {
        ...state,
        playerHand: [...state.playerHand, action.payload.card],
        currentPlayerScore: action.payload.score
      };
    case 'STAND':
      return {
        ...state,
        dealerHand: action.payload.hand,
        currentDealerScore: action.payload.score,
        gameStage: 'result'
      };
    case 'RESET_GAME':
      return {
        ...state,
        balance: state.balance - state.bet,
        bet: 0,
        dealerHand: [],
        playerHand: [],
        currentDealerScore: 0,
        currentPlayerScore: 0,
        gameStage: 'ready'
      };
    default:
      return state;
  }
};

export default store;
