import { configureStore } from '@reduxjs/toolkit';
import gameReducer from './reducers/gameReducer';

const initialState = {
  balance: 1000,
  bet: 0,
  dealerHand: [],
  playerHand: [],
  currentDealerScore: 0,
  currentPlayerScore: 0,
  gameStage: 'ready'
};

// const store = configureStore({
//   reducer: {
//     game: gameReducer,
//   },
//   preloadedState: initialState, // Make sure this value is not undefined
// });

const rootReducer = {
    game: gameReducer,
};

const store = configureStore({
    reducer: rootReducer,
    preloadedState: initialState,
});

export default store;
