import React from 'react';
import Card from './Card';

function Hand({ hand, score, showScore }) {
  return (
    <div className="hand">
      {hand.map((card, index) => (
        <Card key={index} card={card} />
      ))}
      {showScore && <div className="score">{score}</div>}
    </div>
  );
}

export default Hand;
