import React from 'react';
import Card from './Card';
import { useSelector } from 'react-redux';

function Hand({ cards = [], score, isDealer, gameStage }) {
  return (
    <div className={isDealer ? 'dealer-hand' : 'player-hand'}>
      <h2>{isDealer ? 'Dealer' : 'Player'}</h2>
      <div className="cards">
        <span>
            gameStage = ;

            {/* if is dealer and game stage is in progress, show only one card otherwise show all cards */}
            {isDealer && useSelector(state => state.game.gameStage) === 'inProgress' ? (
                <Card card={cards[0]} />
            ) : (
                cards.map((card, index) => (
                    <Card key={index} card={card} />
                ))
            )}

            {/* {cards.map((card, index) => (
                <Card key={index} card={card} />
            ))} */}
        </span>
      </div>
      <div className="score">
        <span>{score}</span>
      </div>
    </div>
  );
}

export default Hand;
