import { getDeck, shuffleDeck, getCardValue } from '../utils/deckUtils';

export const setBet = (bet) => {
  return {
    type: 'SET_BET',
    payload: bet
  };
};

export const dealCards = (bet) => {
  return (dispatch, getState) => {
    const { game } = getState();
    const deck = getDeck();
    const shuffledDeck = shuffleDeck(deck);

    const playerCard1 = deck.pop();
    const dealerCard1 = deck.pop();
    const playerCard2 = deck.pop();
    const hiddenCard = deck.pop();
    const playerScore = getCardValue(playerCard1, 0) + getCardValue(playerCard2, getCardValue(playerCard1, 0));
    const dealerScore = getCardValue(dealerCard1, 0) + getCardValue(hiddenCard, getCardValue(dealerCard1, 0));
    console.log(dealerCard1, hiddenCard, playerCard1, playerCard2, dealerScore, playerScore);
    dispatch({
      type: 'DEAL_CARDS',
      payload: {
        dealerCard: dealerCard1,
        hiddenCard: hiddenCard,
        playerCard1: playerCard1,
        playerCard2: playerCard2,
        dealerScore: dealerScore,
        playerScore: playerScore,
        deck: shuffledDeck
      }
    });
  };
};

export const hit = () => {
  return (dispatch, getState) => {
    const deck = getState().game.deck;
    const playerHand = getState().game.playerHand;
    const currentPlayerScore = getState().game.currentPlayerScore;
    const newCard = deck.pop();
    const newScore = currentPlayerScore + getCardValue(newCard);

    dispatch({
      type: 'HIT',
      payload: {
        card: newCard,
        score: newScore
      }
    });
  };
};

export const stand = () => {
  return (dispatch, getState) => {
    const deck = getState().game.deck;
    const dealerHand = getState().game.dealerHand;
    let dealerScore = getState().game.currentDealerScore;

    // Reveal hidden card and update dealer score
    const hiddenCard = dealerHand[1];
    dealerHand[1] = hiddenCard;
    dealerScore += getCardValue(hiddenCard);

    // Dealer must hit until they have 17 or more
    while (dealerScore < 17) {
      const newCard = deck.pop();
      dealerHand.push(newCard);
      dealerScore += getCardValue(newCard);
    }

    dispatch({
      type: 'STAND',
      payload: {
        hand: dealerHand,
        score: dealerScore
      }
    });
  };
};

export const resetGame = () => {
  return {
    type: 'RESET_GAME'
  };
};
