import React from 'react';

function Controls({ gameStage, hasPair, handleDealClick, handleHitClick, handleStandClick, handleDoubleClick, handleSplitClick, handleResetClick }) {
    return (
        <div className="controls">
        {gameStage === 'ready' && (
            <button className="btn btn-primary" onClick={handleDealClick}>
                Deal
            </button>
        )}
        // if the player had a split, show split button

        {gameStage === 'inProgress' && (
            <>
                <button className="btn btn-primary" onClick={handleHitClick}>
                    Hit
                </button>
                <button className="btn btn-primary" onClick={handleStandClick}>
                    Stand
                </button>
                <button className="btn btn-primary" onClick={handleDoubleClick}>
                    Double
                </button>
            </>
        )}
        {gameStage === 'result' && (
            <>
                <button className="btn btn-secondary" onClick={handleResetClick}>
                    Reset
                </button>
            </>
        )}
        </div>
    );
}

export default Controls;
